
public class Lec6_IncrementDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =10;
		System.out.println(a++);
		
		a++;
		++a;
		System.out.println(a);
		
		int b = a++;
		System.out.println(b);
		System.out.println(a);
		
		a^=2;
		System.out.println(a);
		
		int c = 2* 3/2;
		System.out.println(c);

	}

}
